    private void ConnettiBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConnettiBActionPerformed
        DBConnect.host=HostField.getText();
        try{
            DBConnect.porta=Integer.parseInt(PortField.getText());
        }catch(NumberFormatException e){
            JOptionPane.showMessageDialog(this,"La porta deve necessariamente essere un numero","Errore", JOptionPane.ERROR_MESSAGE);
        }
        DBConnect.schema=SchemaField.getText();
        DBConnect.servizio=ServiceField.getText();
        DBConnect.user=UserField.getText();
        DBConnect.password= PasswField.getText();
        try{
            DBConnect.setDefaultConnection(DBConnect.nuovaConnessione());
            JOptionPane.showMessageDialog(this, "Sei connesso...");
            setVisible(false);
            //this.dispose();  //da controllare
            SecondaPagina seconda= new SecondaPagina();
            seconda.setVisible(true);

        }catch(SQLException e){
            JOptionPane.showMessageDialog(this, "Connessione fallita(Credenziali errate ?)", "Errore", JOptionPane.ERROR_MESSAGE);
        }
        
        
        
    }//GEN-LAST:event_ConnettiBActionPerformed

