    private void CheckBind(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CheckBind
        Binding bind= bindingGroup.getBinding("bindSchema");
        if(bind.isBound()){
            bind.unbind();
        }else{
            bind.bind();
        }
    }//GEN-LAST:event_CheckBind

